package Nov13;

public class switchcase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int day = 7;
			
		switch (day) {
		   case 10:
		    System.out.println("Monday");
		    break;
		   case 4:
			    System.out.println("Monday");
			    break;
		   case 7:
			    System.out.println("Wednesday");
			    break;
		}
		
		String day1="Monday";
		switch (day1) {
		   case "Monday":
		    System.out.println("Second swithchMonday");
		    break;
		   case "Tuesday":
			    System.out.println("Monday");
			    break;
		   case "Wednesday":
			    System.out.println("Wednesday");
			    break;
		}
		
		
		
		
	}

}
